// AllInstancePage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function AllInstancePage() {
  const [instances, setInstances] = useState([]);

  useEffect(() => {
    axios
      .get('http://localhost:5000/api/instances') // Assumes this returns all instances
      .then(res => setInstances(res.data))
      .catch(() => alert("‚ùå Error fetching instances"));
  }, []);

  return (
    <div className="max-w-6xl mx-auto py-10 px-4">
      <h2 className="text-4xl font-bold mb-6 text-center text-gray-900">üìã All Course Instances</h2>

      {instances.length === 0 ? (
        <p className="text-center text-gray-600">No course instances found.</p>
      ) : (
        <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {instances.map(instance => (
            <div key={instance._id} className="bg-white shadow rounded p-6 hover:shadow-md transition">
              <h3 className="text-xl font-semibold text-gray-800 mb-1">{instance.courseId}</h3>
              <p className="text-sm text-gray-600 mb-1">
                <b>Year:</b> {instance.year}
              </p>
              <p className="text-sm text-gray-600 mb-2">
                <b>Semester:</b> {instance.semester}
              </p>
              <Link
                to={`/instances`}
                className="bg-red-600 hover:bg-red-500 text-white px-4 py-2 rounded font-semibold"
              >
                View Instance ‚Üí
              </Link>
            </div>
          ))}
        </div>
      )}

      <div className="text-center mt-10">
        <Link
          to="/"
          className="inline-block bg-gray-700 hover:bg-gray-600 text-white px-6 py-2 rounded font-semibold"
        >
          ‚Üê Back to Dashboard
        </Link>
      </div>
    </div>
  );
}

export default AllInstancePage;
