import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import { useLocation, Link } from 'react-router-dom';

function CourseList({ refresh }) {
  const [courses, setCourses] = useState([]);
  const scrollRef = useRef(null);
  const location = useLocation();
  const isHome = location.pathname === '/';

  const loadCourses = () => {
    axios.get('http://localhost:5000/api/courses')
      .then(res => setCourses(res.data))
      .catch(() => alert("❌ Failed to fetch courses"));
  };

  useEffect(() => {
    loadCourses();
  }, [refresh]);

  const handleDelete = (id) => {
    axios.delete(`http://localhost:5000/api/courses/${id}`)
      .then(() => loadCourses())
      .catch(err =>
        alert("❌ " + (err.response?.data?.message || "Error deleting course"))
      );
  };

  const scroll = (direction) => {
    const scrollAmount = 300;
    if (scrollRef.current) {
      scrollRef.current.scrollBy({
        left: direction === 'left' ? -scrollAmount : scrollAmount,
        behavior: 'smooth',
      });
    }
  };

  return (
    <div className="my-10">
      <h2 className="text-2xl font-bold mb-4 text-gray-800">📚 All Courses</h2>

      {isHome ? (
        <div className="relative">
          <div className="flex items-center justify-between">
            <button
              onClick={() => scroll('left')}
              className="text-4xl text-gray-400 hover:text-gray-600 transition"
              aria-label="Scroll Left"
            >
              ⬅️
            </button>
            <div
              ref={scrollRef}
              className="overflow-x-auto flex gap-6 px-4 py-2 scroll-smooth scrollbar-hide"
              style={{ scrollBehavior: 'smooth' }}
            >
              {courses.map(course => (
                <div
                  key={course.courseId}
                  className="bg-white shadow-sm border border-gray-200 rounded-lg p-4 hover:shadow-md transition min-w-[250px]"
                >
                  <h3 className="text-lg font-semibold text-red-600">{course.title}</h3>
                  <p className="text-sm text-gray-600 mt-1">
                    <span className="font-medium">ID:</span> {course.courseId}
                  </p>
                  <p className="text-sm text-gray-600 mt-1">
                    <span className="font-medium">Prerequisites:</span>{' '}
                    {course.prerequisites.length > 0
                      ? course.prerequisites.join(', ')
                      : 'None'}
                  </p>
                  <button
                    onClick={() => handleDelete(course.courseId)}
                    className="mt-4 text-sm text-white bg-red-500 hover:bg-red-600 px-3 py-1 rounded"
                  >
                    Delete
                  </button>
                </div>
              ))}
            </div>
            <button
              onClick={() => scroll('right')}
              className="text-4xl text-gray-400 hover:text-gray-600 transition"
              aria-label="Scroll Right"
            >
              ➡️
            </button>
          </div>

          <div className="mt-4 text-center">
            <Link
              to="/courses"
              className="text-blue-600 hover:underline font-medium"
            >
              View All Courses →
            </Link>
          </div>
        </div>
      ) : (
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
          {courses.map(course => (
            <div
              key={course.courseId}
              className="bg-white shadow-sm border border-gray-200 rounded-lg p-4 hover:shadow-md transition"
            >
              <h3 className="text-lg font-semibold text-red-600">{course.title}</h3>
              <p className="text-sm text-gray-600 mt-1">
                <span className="font-medium">ID:</span> {course.courseId}
              </p>
              <p className="text-sm text-gray-600 mt-1">
                <span className="font-medium">Prerequisites:</span>{' '}
                {course.prerequisites.length > 0
                  ? course.prerequisites.join(', ')
                  : 'None'}
              </p>
              <button
                onClick={() => handleDelete(course.courseId)}
                className="mt-4 text-sm text-white bg-red-500 hover:bg-red-600 px-3 py-1 rounded"
              >
                Delete
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default CourseList;
