// CourseForm.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

function CourseForm({ onSuccess }) {
  const [form, setForm] = useState({ title: '', courseId: '', description: '', prerequisites: [] });
  const [allCourses, setAllCourses] = useState([]);

  // Fetch existing courses to show as options for prerequisites
  useEffect(() => {
    axios.get('http://localhost:5000/api/courses')
      .then(res => setAllCourses(res.data))
      .catch(err => console.error("Failed to load courses"));
  }, []);

  const handleSubmit = e => {
    e.preventDefault();
    axios.post('http://localhost:5000/api/courses', form)
      .then(() => {
        alert("‚úÖ Course added successfully");
        setForm({ title: '', courseId: '', description: '', prerequisites: [] });
        onSuccess(); // üîÅ trigger refresh
      })
      .catch(err => {
        alert("‚ùå " + (err.response?.data?.message || "Error creating course"));
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Create a Course</h2>
      <input placeholder="Title" value={form.title} onChange={e => setForm({ ...form, title: e.target.value })} />
      <input placeholder="Course ID" value={form.courseId} onChange={e => setForm({ ...form, courseId: e.target.value })} />
      <input placeholder="Description" value={form.description} onChange={e => setForm({ ...form, description: e.target.value })} />
      <select multiple value={form.prerequisites} onChange={e => {
        const selected = Array.from(e.target.selectedOptions, o => o.value);
        setForm({ ...form, prerequisites: selected });
      }}>
        {allCourses.map(course => (
          <option key={course.courseId} value={course.courseId}>{course.courseId}</option>
        ))}
      </select>
      <button type="submit">Add Course</button>
    </form>
  );
}

export default CourseForm;
