// InstanceList.js
import React, { useState } from 'react';
import axios from 'axios';

function InstanceList() {
  const [year, setYear] = useState('');
  const [semester, setSemester] = useState('');
  const [instances, setInstances] = useState([]);
  const [notFound, setNotFound] = useState(false);

  const fetchInstances = () => {
    if (!year || !semester) {
      return alert("Please enter both year and semester");
    }

    axios.get(`http://localhost:5000/api/instances/${year}/${semester}`)
      .then(res => {
        const data = res.data;
        if (data.length === 0) {
          setNotFound(true);
          setInstances([]);
        } else {
          setNotFound(false);
          setInstances(data);
        }
      })
      .catch(() => {
        alert("❌ Failed to fetch data from server");
      });
  };

  const handleDelete = (courseId) => {
    axios.delete(`http://localhost:5000/api/instances/${year}/${semester}/${courseId}`)
      .then(fetchInstances)
      .catch(() => alert("Failed to delete instance"));
  };

  return (
    <div>
      <h2>Course Instances</h2>
      <input placeholder="Year" value={year} onChange={e => setYear(e.target.value)} />
      <input placeholder="Semester" value={semester} onChange={e => setSemester(e.target.value)} />
      <button onClick={fetchInstances}>View Instances</button>
{notFound ? (
        <p>⚠️ No instances found for Year {year} and Semester {semester}.</p>
      ) : (
        instances.map(i => (
          <div key={i._id}>
            {i.courseId} offered in {i.year}-{i.semester}
            <button onClick={() => handleDelete(i.courseId)}>Delete</button>
          </div>
        ))
      )}
    </div>
  );
}

export default InstanceList;
