import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import { useLocation, Link } from 'react-router-dom';

function CourseList({ refresh }) {
  const [courses, setCourses] = useState([]);
  const location = useLocation();
  const isHome = location.pathname === '/';
  const scrollRef = useRef(null);



  useEffect(() => {
    axios
      .get('http://localhost:5000/api/courses')
      .then(res => setCourses(res.data))
      .catch(() => alert('‚ùå Failed to fetch courses'));
  }, [refresh]);

  const handleDelete = (id) => {
    axios
      .delete(`http://localhost:5000/api/courses/${id}`)
      .then(() => setCourses(prev => prev.filter(c => c.courseId !== id)))
      .catch(err =>
        alert("‚ùå " + (err.response?.data?.message || "Error deleting course"))
      );
  };

  return (
    <div className="my-10">
      <h2 className="text-2xl font-bold mb-4 text-gray-800">üìö All Courses</h2>

      {isHome ? (
        <div className="relative bg-gray-100 rounded-xl px-4 py-6">
          <div
  ref={scrollRef}
  className="flex gap-6 overflow-x-auto scroll-smooth snap-x snap-mandatory pb-4 scrollbar-hide"
>
            {courses.map(course => (
              <div
                key={course.courseId}
                className="min-w-[260px] bg-white snap-start shadow-sm border border-gray-200 rounded-lg p-4 hover:shadow-md transition"
              >
                <h3 className="text-lg font-semibold text-red-600">{course.title}</h3>
                <p className="text-sm text-gray-600 mt-1">
                  <span className="font-medium">ID:</span> {course.courseId}
                </p>
                <p className="text-sm text-gray-600 mt-1">
                  <span className="font-medium">Prerequisites:</span>{' '}
                  {course.prerequisites.length > 0
                    ? course.prerequisites.join(', ')
                    : 'None'}
                </p>
                <button
                  onClick={() => handleDelete(course.courseId)}
                  className="mt-4 text-sm text-white bg-red-500 hover:bg-red-600 px-3 py-1 rounded"
                >
                  Delete
                </button>
              </div>
            ))}
          </div>

          {/* Scroll bar indicator like in image */}
          <div className="h-2 bg-gray-300 rounded-full mt-4 relative overflow-hidden">
            <div className="absolute top-0 left-0 h-full w-[30%] bg-red-500 rounded-full animate-pulse" />
          </div>

          <div className="mt-4 text-center">
            <Link
              to="/courses"
              className="text-blue-600 hover:underline font-medium"
            >
              View All Courses ‚Üí
            </Link>
          </div>
        </div>
      ) : (
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
          {courses.map(course => (
            <div
              key={course.courseId}
              className="bg-white shadow-sm border border-gray-200 rounded-lg p-4 hover:shadow-md transition"
            >
              <h3 className="text-lg font-semibold text-red-600">{course.title}</h3>
              <p className="text-sm text-gray-600 mt-1">
                <span className="font-medium">ID:</span> {course.courseId}
              </p>
              <p className="text-sm text-gray-600 mt-1">
                <span className="font-medium">Prerequisites:</span>{' '}
                {course.prerequisites.length > 0
                  ? course.prerequisites.join(', ')
                  : 'None'}
              </p>
              <button
                onClick={() => handleDelete(course.courseId)}
                className="mt-4 text-sm text-white bg-red-500 hover:bg-red-600 px-3 py-1 rounded"
              >
                Delete
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default CourseList;
