import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import CourseForm from './components/CourseForm';
import CourseList from './components/CourseList';
import InstanceForm from './components/InstanceForm';
import InstanceList from './components/InstanceList';
import CourseDetail from './components/CourseDetail';
import AllCoursePage from './components/AllCoursePage';
import NavBar from './components/NavBar';

function App() {
  const [refreshCourses, setRefreshCourses] = useState(false);

  return (
    <Route
  path="/"
  element={
    <div className="space-y-10">
      {/* Hero Section */}
      <div className="text-center py-12 bg-white shadow rounded">
        <h2 className="text-4xl font-bold text-gray-900 mb-2">
          Manage Your Courses Efficiently
        </h2>
        <p className="text-gray-600 text-lg">Create, View, and Organize All Courses and Instances</p>
        <div className="mt-6 flex justify-center gap-4">
          <Link
            to="/"
            className="bg-red-600 hover:bg-red-500 text-white px-6 py-2 rounded font-semibold"
          >
            + Add Course
          </Link>
          <Link
            to="/courses"
            className="bg-gray-800 hover:bg-gray-700 text-white px-6 py-2 rounded font-semibold"
          >
            View All Courses
          </Link>
        </div>
      </div>

      {/* Course Section */}
      <section className="bg-white p-6 rounded shadow">
        <h3 className="text-2xl font-semibold text-gray-800 mb-4">ðŸ“˜ Manage Courses</h3>
        <CourseForm onSuccess={() => setRefreshCourses(prev => !prev)} />
        <CourseList refresh={refreshCourses} />
      </section>

      {/* Instance Section */}
      <section className="bg-white p-6 rounded shadow">
        <h3 className="text-2xl font-semibold text-gray-800 mb-4">ðŸ“— Manage Instances</h3>
        <InstanceForm />
        <InstanceList />
      </section>
    </div>
  }
/>

  );
}

export default App;
