// CourseList.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

function CourseList({refresh}) {
  const [courses, setCourses] = useState([]);

  const loadCourses = () => {
    axios.get('http://localhost:5000/api/courses')
      .then(res => setCourses(res.data))
      .catch(err => alert("Failed to fetch courses"));
  };

  useEffect(() => {
    loadCourses();
  }, [refresh]);

  const handleDelete = (id) => {
    axios.delete(`http://localhost:5000/api/courses/${id}`)
      .then(() => loadCourses())
      .catch(err => alert("❌ " + (err.response?.data?.message || "Error deleting course")));
  };

  return (
    <div>
      <h2>All Courses</h2>
      {courses.map(course => (
        <div key={course.courseId}>
          <b>{course.courseId}</b> - {course.title}
          <br />
          Prerequisites: {course.prerequisites.join(', ') || 'None'}
          <br />
          <button onClick={() => handleDelete(course.courseId)}>Delete</button>
        </div>
      ))}
    </div>
  );
}

export default CourseList;
