// InstanceList.js
import React, { useState } from 'react';
import axios from 'axios';

function InstanceList() {
  const [year, setYear] = useState('');
  const [semester, setSemester] = useState('');
  const [instances, setInstances] = useState([]);
  const [notFound, setNotFound] = useState(false);

  const fetchInstances = () => {
    if (!year || !semester) {
      return alert("Please enter both year and semester");
    }

    axios
      .get(`http://localhost:5000/api/instances/${year}/${semester}`)
      .then(res => {
        const data = res.data;
        if (data.length === 0) {
          setNotFound(true);
          setInstances([]);
        } else {
          setNotFound(false);
          setInstances(data);
        }
      })
      .catch(() => {
        alert("‚ùå Failed to fetch data from server");
      });
  };

  const handleDelete = (courseId) => {
    axios
      .delete(`http://localhost:5000/api/instances/${year}/${semester}/${courseId}`)
      .then(fetchInstances)
      .catch(() => alert("Failed to delete instance"));
  };

  return (
    <div className="my-10">
      <h2 className="text-2xl font-bold mb-4 text-gray-800">üéì Course Instances</h2>
      <div className="flex gap-4 mb-4">
        <input
          type="text"
          placeholder="Year"
          value={year}
          onChange={e => {
    setYear(e.target.value);
    setNotFound(false); // ‚úÖ Reset warning on edit
  }}
          className="border border-gray-300 rounded px-3 py-2"
        />
        <input
          type="text"
          placeholder="Semester"
          value={semester}
          onChange={e => setSemester(e.target.value)}
          className="border border-gray-300 rounded px-3 py-2"
        />
        <button
          onClick={fetchInstances}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          View Instances
        </button>
      </div>

      {notFound ? (
        <p className="text-yellow-700 font-semibold">
          ‚ö†Ô∏è No instances found for Year {year} and Semester {semester}.
        </p>
      ) : (
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
          {instances.map(i => (
            <div
              key={i._id}
              className="bg-white border border-gray-200 rounded-lg p-4 shadow-sm"
            >
              <p className="text-red-600 font-semibold">{i.courseId}</p>
              <p className="text-sm text-gray-700">
                Offered in {i.year} - {i.semester}
              </p>
              <p className="text-sm text-gray-500">
                Instructor: {i.instructor}
              </p>
              <button
                onClick={() => handleDelete(i.courseId)}
                className="mt-3 text-sm bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
              >
                Delete
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default InstanceList;
