// CourseDetail.js
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

function CourseDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [course, setCourse] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    axios.get(`http://localhost:5000/api/courses/${id}`)
      .then(res => setCourse(res.data))
      .catch(() => setError("‚ùå Course not found"));
  }, [id]);

  if (error) {
    return <p className="text-red-600 text-center mt-10">{error}</p>;
  }

  if (!course) {
    return <p className="text-center text-gray-500 mt-10">Loading...</p>;
  }

  return (
    <div className="max-w-3xl mx-auto bg-white rounded shadow p-8 mt-10">
      <h2 className="text-3xl font-bold text-gray-800 mb-4">üìÑ Course Details</h2>
      <p className="mb-2"><span className="font-semibold text-gray-700">Course ID:</span> {course.courseId}</p>
      <p className="mb-2"><span className="font-semibold text-gray-700">Title:</span> {course.title}</p>
      <p className="mb-2"><span className="font-semibold text-gray-700">Description:</span> {course.description || "None"}</p>
      <p className="mb-4">
        <span className="font-semibold text-gray-700">Prerequisites:</span>{' '}
        {course.prerequisites.length > 0
          ? course.prerequisites.join(', ')
          : "None"}
      </p>
      <button
        onClick={() => navigate("/courses")}
        className="bg-red-600 hover:bg-red-500 text-white px-4 py-2 rounded font-semibold"
      >
        ‚Üê Back to Courses
      </button>
    </div>
  );
}

export default CourseDetail;
