// InstanceForm.js
import React, { useState } from 'react';
import axios from 'axios';

function InstanceForm() {
  const [instance, setInstance] = useState({ year: '', semester: '', courseId: '' });

  const handleSubmit = e => {
    e.preventDefault();
    axios.post('http://localhost:5000/api/instances', instance)
      .then(() => {
        alert("✅ Instance created");
        setInstance({ year: '', semester: '', courseId: '' });
      })
      .catch(() => alert("❌ Failed to create instance"));
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Create Course Instance</h2>
      <input placeholder="Year" value={instance.year} onChange={e => setInstance({ ...instance, year: e.target.value })} />
      <input placeholder="Semester" value={instance.semester} onChange={e => setInstance({ ...instance, semester: e.target.value })} />
      <input placeholder="Course ID" value={instance.courseId} onChange={e => setInstance({ ...instance, courseId: e.target.value })} />
      <button type="submit">Create Instance</button>
    </form>
  );
}

export default InstanceForm;
