import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import { Link, useLocation } from 'react-router-dom';
import { FaAngleDoubleLeft, FaAngleDoubleRight } from 'react-icons/fa';

function InstanceList() {
  const [year, setYear] = useState('');
  const [semester, setSemester] = useState('');
  const [instances, setInstances] = useState([]);
  const [notFound, setNotFound] = useState(false);
  const scrollRef = useRef(null);
  const [scrollProgress, setScrollProgress] = useState(0);
  const location = useLocation();
  const isHome = location.pathname === '/';

  const fetchInstances = () => {
    if (!year || !semester) {
      return alert("Please enter both year and semester");
    }

    axios
      .get(`http://localhost:5000/api/instances/${year}/${semester}`)
      .then(res => {
        const data = res.data;
        if (data.length === 0) {
          setNotFound(true);
          setInstances([]);
        } else {
          setNotFound(false);
          setInstances(data);
        }
      })
      .catch(() => {
        alert("❌ Failed to fetch data from server");
      });
  };

  const handleDelete = (courseId) => {
    axios
      .delete(`http://localhost:5000/api/instances/${year}/${semester}/${courseId}`)
      .then(fetchInstances)
      .catch(() => alert("Failed to delete instance"));
  };

  useEffect(() => {
  const container = scrollRef.current;
  if (!container) return;

  const handleScroll = () => {
    const scrollLeft = container.scrollLeft;
    const maxScrollLeft = container.scrollWidth - container.clientWidth;
    const percentage = maxScrollLeft === 0 ? 0 : (scrollLeft / maxScrollLeft) * 100;
    setScrollProgress(percentage);
  };

  container.addEventListener('scroll', handleScroll);

  // ✅ Trigger once after render to initialize progress bar
  setTimeout(() => {
    handleScroll();
  }, 100);

  return () => {
    container.removeEventListener('scroll', handleScroll);
  };
}, [instances]); // ✅ rerun every time new data (cards) are loaded


  // Auto scroll
  useEffect(() => {
    const interval = setInterval(() => {
      const container = scrollRef.current;
      if (!container) return;
      const maxScrollLeft = container.scrollWidth - container.clientWidth;
      if (Math.ceil(container.scrollLeft) >= maxScrollLeft) {
        container.scrollTo({ left: 0, behavior: 'smooth' });
      } else {
        container.scrollBy({ left: 300, behavior: 'smooth' });
      }
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  const scrollLeft = () => {
    scrollRef.current?.scrollBy({ left: -300, behavior: 'smooth' });
  };
  const scrollRight = () => {
    scrollRef.current?.scrollBy({ left: 300, behavior: 'smooth' });
  };

  return (
    <div className="my-10">
      <h2 className="text-2xl font-bold mb-4 text-gray-800">🎓 Course Instances</h2>

      <div className="flex gap-4 mb-4">
        <input
          type="text"
          placeholder="Year"
          value={year}
          onChange={e => {
            setYear(e.target.value);
            setNotFound(false);
          }}
          className="border border-gray-300 rounded px-3 py-2"
        />
        <input
          type="text"
          placeholder="Semester"
          value={semester}
          onChange={e => {
            setSemester(e.target.value);
            setNotFound(false);
          }}
          className="border border-gray-300 rounded px-3 py-2"
        />
        <button
          onClick={fetchInstances}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          View Instances
        </button>
      </div>

      {notFound ? (
        <p className="text-yellow-700 font-semibold">
          ⚠️ No instances found for Year {year} and Semester {semester}.
        </p>
      ) : (
        instances.length > 0 && (
          <div className="relative bg-gray-100 rounded-xl px-4 py-6">
            {/* Scrollable cards */}
            <div
              ref={scrollRef}
              className="flex gap-6 overflow-x-auto scroll-smooth snap-x snap-mandatory pb-4 scrollbar-hide"
            >
              {instances.map(i => (
                <div
                  key={i._id}
                  className="min-w-[260px] snap-start bg-white border border-gray-200 rounded-lg p-4 shadow-sm"
                >
                  <p className="text-red-600 font-semibold text-lg">{i.courseId}</p>
                  <p className="text-sm text-gray-700">Year: {i.year}</p>
                  <p className="text-sm text-gray-700 mb-2">Semester: {i.semester}</p>
                  <button
                    onClick={() => handleDelete(i.courseId)}
                    className="mt-2 text-sm bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
                  >
                    Delete
                  </button>
                </div>
              ))}
            </div>

            {/* Arrows */}
            <button
              className="absolute top-1/2 left-[-20px] transform -translate-y-1/2 bg-white border rounded-full p-2 shadow hover:bg-gray-100 text-xl z-10"
              onClick={scrollLeft}
            >
              <FaAngleDoubleLeft />
            </button>
            <button
              className="absolute top-1/2 right-[-20px] transform -translate-y-1/2 bg-white border rounded-full p-2 shadow hover:bg-gray-100 text-xl z-10"
              onClick={scrollRight}
            >
              <FaAngleDoubleRight />
            </button>

                      {/* Scroll bar indicator like in image */}
                      <div className="h-2 bg-gray-300 rounded-full mt-4 relative overflow-hidden">
                        <div
                className="absolute top-0 left-0 h-full bg-red-500 rounded-full transition-all duration-200"
                style={{ width: `${scrollProgress}%` }}
              />
                      </div>

            {/* ✅ View All Instances */}
            <div className="mt-4 text-center">
              <Link
                to="/instances"
                className="text-blue-600 hover:underline font-medium"
              >
                View All Instances →
              </Link>
            </div>
          </div>
        )
      )}
    </div>
  );
}

export default InstanceList;
