// AllCoursePage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function AllCoursePage() {
  const [courses, setCourses] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/api/courses")
      .then(res => setCourses(res.data))
      .catch(() => alert("‚ùå Error fetching courses"));
  }, []);

  return (
    <div className="max-w-6xl mx-auto py-10 px-4">
      <h2 className="text-4xl font-bold mb-6 text-center text-gray-900">üìã All Courses</h2>
      {courses.length === 0 ? (
        <p className="text-center text-gray-600">No courses found.</p>
      ) : (
        <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {courses.map(course => (
            <div key={course.courseId} className="bg-white shadow rounded p-6 hover:shadow-md transition">
              <h3 className="text-xl font-semibold text-gray-800 mb-1">{course.title}</h3>
              <p className="text-sm text-gray-600 mb-1"><b>ID:</b> {course.courseId}</p>
              <p className="text-sm text-gray-600 mb-2">
                <b>Prerequisites:</b>{' '}
                {course.prerequisites.length > 0
                  ? course.prerequisites.join(', ')
                  : "None"}
              </p>
              <Link
                to={`/courses/${course.courseId}`}
                className="text-white bg-indigo-600 hover:bg-indigo-500 px-4 py-2 text-sm rounded font-medium"
              >
                View Details ‚Üí
              </Link>
            </div>
          ))}
        </div>
      )}
      <div className="text-center mt-10">
        <Link
          to="/"
          className="inline-block bg-gray-700 hover:bg-gray-600 text-white px-6 py-2 rounded font-semibold"
        >
          ‚Üê Back to Dashboard
        </Link>
      </div>
    </div>
  );
}

export default AllCoursePage;
