// App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import CourseForm from './components/CourseForm';
import CourseList from './components/CourseList';
import InstanceForm from './components/InstanceForm';
import InstanceList from './components/InstanceList';
import CourseDetail from './components/CourseDetail';
import AllCoursePage from './components/AllCoursePage';
import NavBar from './components/NavBar'; // âœ… Add this file

function App() {
  const [refreshCourses, setRefreshCourses] = useState(false);

  return (
    <Router>
      <div className="min-h-screen bg-gray-50 text-gray-800">
        <NavBar />

        <div className="p-6 max-w-6xl mx-auto">
          <Routes>
            {/* Main Dashboard */}
            <Route
              path="/"
              element={
                <div className="space-y-8">
                  <h1 className="text-3xl font-bold text-indigo-600">ðŸ“˜ Course Management System</h1>
                  <CourseForm onSuccess={() => setRefreshCourses(prev => !prev)} />
                  <CourseList refresh={refreshCourses} />
                  <InstanceForm />
                  <InstanceList />
                </div>
              }
            />

            {/* All Courses Page */}
            <Route path="/courses" element={<AllCoursePage />} />

            {/* Single Course Detail */}
            <Route path="/courses/:id" element={<CourseDetail />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
