import React, { useState } from 'react';
import axios from 'axios';

function InstanceList() {
  const [year, setYear] = useState('');
  const [semester, setSemester] = useState('');
  const [instances, setInstances] = useState([]);
  const [notFound, setNotFound] = useState(false);

  const fetchInstances = () => {
    if (!year || !semester) {
      return alert("Please enter both year and semester");
    }

    axios
      .get(`http://localhost:5000/api/instances/${year}/${semester}`)
      .then(res => {
        const data = res.data;
        setNotFound(data.length === 0);
        setInstances(data);
      })
      .catch(() => {
        alert("‚ùå Failed to fetch data from server");
      });
  };

  const handleDelete = (courseId) => {
    axios
      .delete(`http://localhost:5000/api/instances/${year}/${semester}/${courseId}`)
      .then(fetchInstances)
      .catch(() => alert("Failed to delete instance"));
  };

  const handleYearChange = (e) => {
    setYear(e.target.value);
    setNotFound(false); // Reset message when input changes
  };

  const handleSemesterChange = (e) => {
    setSemester(e.target.value);
    setNotFound(false);
  };

  return (
    <div className="my-10">
      <h2 className="text-2xl font-bold mb-6 text-gray-800 text-center">üéì Course Instances</h2>

      <div className="flex flex-wrap justify-center gap-4 mb-6">
        <input
          type="text"
          placeholder="Year"
          value={year}
          onChange={handleYearChange}
          className="border border-gray-300 rounded px-4 py-2 w-40 shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-300"
        />
        <input
          type="text"
          placeholder="Semester"
          value={semester}
          onChange={handleSemesterChange}
          className="border border-gray-300 rounded px-4 py-2 w-40 shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-300"
        />
        <button
          onClick={fetchInstances}
          className="bg-blue-600 text-white px-5 py-2 rounded hover:bg-blue-700 shadow-md"
        >
          View Instances
        </button>
      </div>

      {notFound ? (
        <p className="text-yellow-700 font-semibold text-center">
          ‚ö†Ô∏è No instances found for Year <strong>{year}</strong> and Semester <strong>{semester}</strong>.
        </p>
      ) : (
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
          {instances.map(i => (
            <div
              key={i._id}
              className="bg-white border border-gray-200 rounded-lg p-4 shadow hover:shadow-md transition"
            >
              <p className="text-lg text-red-600 font-semibold">{i.courseId}</p>
              <p className="text-sm text-gray-700 mt-1">
                <span className="font-medium">Offered in:</span> {i.year} - {i.semester}
              </p>

              <button
                onClick={() => handleDelete(i.courseId)}
                className="mt-4 text-sm bg-red-500 text-white px-4 py-1 rounded hover:bg-red-600"
              >
                Delete
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default InstanceList;
