// CourseList.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useLocation } from 'react-router-dom';

function CourseList({ refresh }) {
  const [courses, setCourses] = useState([]);
  const location = useLocation(); // üìç detect current route

  const loadCourses = () => {
    axios.get('http://localhost:5000/api/courses')
      .then(res => setCourses(res.data))
      .catch(() => alert("‚ùå Failed to fetch courses"));
  };

  useEffect(() => {
    loadCourses();
  }, [refresh]);

  const handleDelete = (id) => {
    axios.delete(`http://localhost:5000/api/courses/${id}`)
      .then(() => loadCourses())
      .catch(err =>
        alert("‚ùå " + (err.response?.data?.message || "Error deleting course"))
      );
  };

  // Only show 5 on home page
  const isHomePage = location.pathname === '/';
  const displayedCourses = isHomePage ? courses.slice(0, 5) : courses;

  return (
    <div className="my-10">
      <h2 className="text-2xl font-bold mb-4 text-gray-800">
        üìö {isHomePage ? 'Top Courses' : 'All Courses'}
      </h2>
      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
        {displayedCourses.map(course => (
          <div
            key={course.courseId}
            className="bg-white shadow-sm border border-gray-200 rounded-lg p-4 hover:shadow-md transition"
          >
            <h3 className="text-lg font-semibold text-red-600">{course.title}</h3>
            <p className="text-sm text-gray-600 mt-1">
              <span className="font-medium">ID:</span> {course.courseId}
            </p>
            <p className="text-sm text-gray-600 mt-1">
              <span className="font-medium">Prerequisites:</span>{' '}
              {course.prerequisites.length > 0
                ? course.prerequisites.join(', ')
                : 'None'}
            </p>
            {/* Show delete only on /courses */}
            {!isHomePage && (
              <button
                onClick={() => handleDelete(course.courseId)}
                className="mt-4 text-sm text-white bg-red-500 hover:bg-red-600 px-3 py-1 rounded"
              >
                Delete
              </button>
            )}
          </div>
        ))}

        {/* +More Card only on home if > 5 courses */}
        {isHomePage && courses.length > 5 && (
          <Link
            to="/courses"
            className="bg-white border-2 border-dashed border-gray-300 hover:border-gray-400 rounded-lg flex items-center justify-center text-blue-600 font-semibold text-lg"
          >
            + More
          </Link>
        )}
      </div>
    </div>
  );
}

export default CourseList;
