// CourseDetail.js
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

function CourseDetail() {
  const { id } = useParams();         // üß† course ID from URL
  const navigate = useNavigate();    // üîô for back button
  const [course, setCourse] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    axios.get(`http://localhost:5000/api/courses/${id}`)
      .then(res => setCourse(res.data))
      .catch(() => setError("‚ùå Course not found"));
  }, [id]);

  if (error) return <p>{error}</p>;

  if (!course) return <p>Loading...</p>;

  return (
    <div>
      <h2>üìÑ Course Details</h2>
      <p><b>Course ID:</b> {course.courseId}</p>
      <p><b>Title:</b> {course.title}</p>
      <p><b>Description:</b> {course.description || "None"}</p>
      <p><b>Prerequisites:</b> {course.prerequisites.length > 0 ? course.prerequisites.join(', ') : "None"}</p>
      <button onClick={() => navigate("/")}>‚Üê Back</button>
    </div>
  );
}

export default CourseDetail;
