import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import CourseForm from './components/CourseForm';
import CourseList from './components/CourseList';
import InstanceForm from './components/InstanceForm';
import InstanceList from './components/InstanceList';
import CourseDetail from './components/CourseDetail';
import AllCoursePage from './components/AllCoursePage';
import NavBar from './components/NavBar';
import Modal from './components/Modal'; // ✅ Import Modal

function App() {
  const [refreshCourses, setRefreshCourses] = useState(false);
  const [showCourseModal, setShowCourseModal] = useState(false);
  const [showInstanceModal, setShowInstanceModal] = useState(false);
  const [showInstanceList, setShowInstanceList] = useState(false);


  return (
    <Router>
      <div className="min-h-screen bg-gray-50 text-gray-800">
        <NavBar
  onAddCourseClick={() => setShowCourseModal(true)}
  onToggleInstances={() => setShowInstanceList(prev => !prev)}
/>

        <div className="p-6 max-w-6xl mx-auto">
          <Routes>
            <Route
              path="/"
              element={
                <div className="space-y-10">
                  {/* Hero Section */}
                  <div className="text-center py-12 bg-white shadow rounded">
                    <h2 className="text-4xl font-bold text-gray-900 mb-2">
                      Manage Your Courses Efficiently
                    </h2>
                    <p className="text-gray-600 text-lg">Welcome to Course Management</p>
                    <div className="mt-6 flex justify-center gap-4">
                      <button
                        onClick={() => setShowCourseModal(true)}
                        className="bg-red-600 hover:bg-red-500 text-white px-6 py-2 rounded font-semibold"
                      >
                        + Create Course
                      </button>
                      <button
                        onClick={() => setShowInstanceModal(true)}
                        className="bg-indigo-600 hover:bg-indigo-500 text-white px-6 py-2 rounded font-semibold"
                      >
                        + Create Instance
                      </button>
                      <Link
                        to="/courses"
                        className="bg-gray-700 hover:bg-gray-600 text-white px-6 py-2 rounded font-semibold"
                      >
                        View All Courses
                      </Link>
                    </div>
                  </div>

                  {/* Lists */}
                  <CourseList refresh={refreshCourses} />
                  <InstanceList />

                  {/* Modals */}
                  <Modal isOpen={showCourseModal} onClose={() => setShowCourseModal(false)}>
                    <CourseForm
                      onSuccess={() => {
                        setRefreshCourses(prev => !prev);
                        setShowCourseModal(false);
                      }}
                    />
                  </Modal>

                  <Modal isOpen={showInstanceModal} onClose={() => setShowInstanceModal(false)}>
                    <InstanceForm onSuccess={() => setShowInstanceModal(false)} />
                  </Modal>
                </div>
              }
            />
            <Route path="/courses" element={<AllCoursePage />} />
            <Route path="/courses/:id" element={<CourseDetail />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
