// CourseList.js
import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import { Link, useLocation } from 'react-router-dom';

function CourseList({ refresh }) {
  const [courses, setCourses] = useState([]);
  const location = useLocation();
  const scrollRef = useRef(null);

  const isHome = location.pathname === '/';

  useEffect(() => {
    axios
      .get('http://localhost:5000/api/courses')
      .then(res => setCourses(res.data))
      .catch(() => alert('❌ Failed to fetch courses'));
  }, [refresh]);

  const handleDelete = (id) => {
    axios
      .delete(`http://localhost:5000/api/courses/${id}`)
      .then(() => {
        setCourses(prev => prev.filter(c => c.courseId !== id));
      })
      .catch(err =>
        alert("❌ " + (err.response?.data?.message || "Error deleting course"))
      );
  };

  const scroll = (direction) => {
    if (!scrollRef.current) return;
    const { scrollLeft, clientWidth } = scrollRef.current;
    const scrollAmount = clientWidth - 40;
    scrollRef.current.scrollTo({
      left: direction === 'left' ? scrollLeft - scrollAmount : scrollLeft + scrollAmount,
      behavior: 'smooth',
    });
  };

  return (
    <div className="my-10">
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-2xl font-bold text-gray-800">📚 All Courses</h2>
        {!isHome && (
          <Link
            to="/"
            className="text-blue-600 hover:underline text-sm font-medium"
          >
            ← Back to Home
          </Link>
        )}
      </div>

      {isHome ? (
        <div className="relative">
          <button
            onClick={() => scroll('left')}
            className="absolute left-0 top-1/2 -translate-y-1/2 z-10 bg-white border border-gray-300 rounded-full w-9 h-9 flex items-center justify-center shadow hover:bg-gray-100"
          >
            ⬅️
          </button>
          <div
            ref={scrollRef}
            className="overflow-x-auto whitespace-nowrap px-12 scroll-smooth"
          >
            <div className="inline-flex gap-6">
              {courses.map(course => (
                <div
                  key={course.courseId}
                  className="min-w-[250px] bg-white shadow-sm border border-gray-200 rounded-lg p-4 hover:shadow-md transition"
                >
                  <h3 className="text-lg font-semibold text-red-600">{course.title}</h3>
                  <p className="text-sm text-gray-600 mt-1">
                    <span className="font-medium">ID:</span> {course.courseId}
                  </p>
                  <p className="text-sm text-gray-600 mt-1">
                    <span className="font-medium">Prerequisites:</span>{' '}
                    {course.prerequisites.length > 0
                      ? course.prerequisites.join(', ')
                      : 'None'}
                  </p>
                  <button
                    onClick={() => handleDelete(course.courseId)}
                    className="mt-4 text-sm text-white bg-red-500 hover:bg-red-600 px-3 py-1 rounded"
                  >
                    Delete
                  </button>
                </div>
              ))}
            </div>
          </div>
          <button
            onClick={() => scroll('right')}
            className="absolute right-0 top-1/2 -translate-y-1/2 z-10 bg-white border border-gray-300 rounded-full w-9 h-9 flex items-center justify-center shadow hover:bg-gray-100"
          >
            ➡️
          </button>
        </div>
      ) : (
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
          {courses.map(course => (
            <div
              key={course.courseId}
              className="bg-white shadow-sm border border-gray-200 rounded-lg p-4 hover:shadow-md transition"
            >
              <h3 className="text-lg font-semibold text-red-600">{course.title}</h3>
              <p className="text-sm text-gray-600 mt-1">
                <span className="font-medium">ID:</span> {course.courseId}
              </p>
              <p className="text-sm text-gray-600 mt-1">
                <span className="font-medium">Prerequisites:</span>{' '}
                {course.prerequisites.length > 0
                  ? course.prerequisites.join(', ')
                  : 'None'}
              </p>
              <button
                onClick={() => handleDelete(course.courseId)}
                className="mt-4 text-sm text-white bg-red-500 hover:bg-red-600 px-3 py-1 rounded"
              >
                Delete
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default CourseList;
