// App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import CourseForm from './components/CourseForm';
import CourseList from './components/CourseList';
import InstanceForm from './components/InstanceForm';
import InstanceList from './components/InstanceList';
import CourseDetail from './components/CourseDetail';
import AllCoursePage from './components/AllCoursePage';
import NavBar from './components/NavBar'; // ✅ Add this file

function App() {
  const [refreshCourses, setRefreshCourses] = useState(false);

  return (
    <Route
  path="/"
  element={
    <div className="space-y-10">
      {/* Hero Section */}
      <div className="text-center py-12 bg-white shadow rounded">
        <h2 className="text-4xl font-bold text-gray-900 mb-2">Manage Your Courses Efficiently</h2>
        <p className="text-gray-600 text-lg">Welcome to Course Management</p>
        <div className="mt-6 flex justify-center gap-4">
          <Link
            to="/"
            className="bg-red-600 hover:bg-red-500 text-white px-6 py-2 rounded font-semibold"
          >
            Create Course
          </Link>
          <Link
            to="/courses"
            className="bg-red-600 hover:bg-red-500 text-white px-6 py-2 rounded font-semibold"
          >
            View All Courses
          </Link>
        </div>
      </div>

      {/* Your CourseForm, CourseList, etc. here */}
      <CourseForm onSuccess={() => setRefreshCourses(prev => !prev)} />
      <CourseList refresh={refreshCourses} />
      <InstanceForm />
      <InstanceList />
    </div>
  }
/>

  );
}

export default App;
