// InstanceDetail.js
import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';

function InstanceDetail() {
  const { year, semester, id } = useParams();
  const [instance, setInstance] = useState(null);
  const [course, setCourse] = useState(null);
  const [error, setError] = useState(false);

  useEffect(() => {
    // Fetch instance
    axios.get(`http://localhost:5000/api/instances/${year}/${semester}/${id}`)
      .then(res => {
        setInstance(res.data);
        return axios.get(`http://localhost:5000/api/courses/${id}`);
      })
      .then(res => {
        setCourse(res.data);
      })
      .catch(() => {
        setError(true);
      });
  }, [year, semester, id]);

  if (error) {
    return <p className="text-center text-red-600 mt-10">‚ùå Error loading instance or course.</p>;
  }

  if (!instance || !course) {
    return <p className="text-center text-gray-500 mt-10">Loading instance details...</p>;
  }

  return (
    <div className="max-w-3xl mx-auto py-10 px-4">
      <h2 className="text-3xl font-bold text-gray-900 mb-4">üìò Instance Details</h2>

      <div className="bg-white rounded shadow p-6 mb-6">
        <h3 className="text-2xl font-semibold text-gray-800 mb-2">{course.title} ({instance.courseId})</h3>
        <p className="text-gray-600 mb-1"><b>Year:</b> {instance.year}</p>
        <p className="text-gray-600 mb-3"><b>Semester:</b> {instance.semester}</p>
        <p className="text-gray-700"><b>Course Description:</b> {course.description || "N/A"}</p>
        <p className="text-gray-700 mt-2">
          <b>Prerequisites:</b> {course.prerequisites.length > 0 ? course.prerequisites.join(', ') : "None"}
        </p>
      </div>

      <Link
        to="/instances"
        className="inline-block bg-gray-700 hover:bg-gray-600 text-white px-5 py-2 rounded"
      >
        ‚Üê Back to All Instances
      </Link>
    </div>
  );
}

export default InstanceDetail;
