// AllCoursesPage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function AllCoursePage() {
  const [courses, setCourses] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/api/courses")
      .then(res => setCourses(res.data))
      .catch(() => alert("Error fetching courses"));
  }, []);

  return (
    <div>
      <h2>üìã All Courses</h2>
      {courses.length === 0 ? <p>No courses found.</p> : (
        courses.map(course => (
          <div key={course.courseId} style={{ marginBottom: "1rem" }}>
            <strong>{course.courseId}</strong> ‚Äì {course.title}<br />
            Prerequisites: {course.prerequisites.length ? course.prerequisites.join(', ') : 'None'}<br />
            <Link to={`/courses/${course.courseId}`}>
              View Details
            </Link>
          </div>
        ))
      )}
      <br />
      <Link to="/">‚Üê Back to Dashboard</Link>
    </div>
  );
}

export default AllCoursePage;
